import React, { useState, useEffect } from 'react';
import {
  Typography,
  TextField,
  Button,
  Stepper,
  Step,
  StepLabel,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
} from '@material-ui/core';
import { DatePicker, LocalizationProvider } from '@mui/x-date-pickers';
import { AdapterDateFns } from '@mui/x-date-pickers';

import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  button: {
    marginRight: theme.spacing(1),
  },
}));

const Firstpage = () => {
  const classes = useStyles();
  const [activeStep, setActiveStep] = useState(0);
  const [skippedSteps, setSkippedSteps] = useState([]);
  const [value, setValue] = useState('Two');
  const [selectedDates, setSelectedDates] = useState([null, null]);
  const [vehicleTypes, setVehicleTypes] = useState([]);
  const [selectedVehicleType, setSelectedVehicleType] = useState('');
  const [vehicleModels, setVehicleModels] = useState([]);
  const [selectedModel, setSelectedModel] = useState('');

  useEffect(() => {
    // Fetch initial list of vehicle types when the component mounts
    // Replace this with your actual API call to fetch from the database
    fetchVehicleTypes();
  }, []);

  const fetchVehicleTypes = () => {
    // Replace this with your actual API call to fetch vehicle types from the database
    // Set the fetched vehicle types to the state
    const fetchedVehicleTypes = ['Sedan', 'SUV', 'Hatchback']; // Replace with your data
    setVehicleTypes(fetchedVehicleTypes);
  };

  const fetchVehicleModels = () => {
    // Replace this with your actual API call to fetch vehicle models from the database
    // Pass the selected vehicle type to the API to get filtered models
    // Set the fetched vehicle models to the state
    const fetchedVehicleModels = ['Model1', 'Model2', 'Model3']; // Replace with your data
    setVehicleModels(fetchedVehicleModels);
  };

  const handleChange = (event) => {
    setValue(event.target.value);
  };

  const handleDateChange = (newDates) => {
    setSelectedDates(newDates);
  };

  const handleVehicleTypeChange = (event) => {
    setSelectedVehicleType(event.target.value);
    // Fetch models based on the selected vehicle type
    fetchVehicleModels();
  };

  const handleModelChange = (event) => {
    setSelectedModel(event.target.value);
  };

  function getSteps() {
    return ['What is your Name', 'Number of wheels', 'Type of vehicle', 'Specific Model', 'Date Range'];
  }

  function getStepContent(step) {
    switch (step) {
      case 0:
        return (
          <>
            <TextField
              id="first-name"
              label="First Name"
              variant="outlined"
              placeholder="Enter Your First Name"
              fullWidth
              margin="normal"
              name="firstName"
            />
            <TextField
              id="last-name"
              label="Last Name"
              variant="outlined"
              placeholder="Enter Your Last Name"
              fullWidth
              margin="normal"
              name="lastName"
            />
          </>
        );

      case 1:
        return (
          <>
            <FormControl>
              <RadioGroup
                aria-labelledby="demo-controlled-radio-buttons-group"
                name="controlled-radio-buttons-group"
                value={value}
                onChange={handleChange}
              >
                <FormControlLabel value="Two" control={<Radio />} label="2 Wheels" />
                <FormControlLabel value="Four" control={<Radio />} label="4 Wheels" />
              </RadioGroup>
            </FormControl>
          </>
        );
      case 2:
        return (
          <>
            <FormControl>
              <RadioGroup
                aria-labelledby="demo-controlled-radio-buttons-group"
                name="controlled-radio-buttons-group"
                value={selectedVehicleType}
                onChange={handleVehicleTypeChange}
              >
                {vehicleTypes.map((type) => (
                  <FormControlLabel key={type} value={type} control={<Radio />} label={type} />
                ))}
              </RadioGroup>
            </FormControl>
          </>
        );
      case 3:
        return (
          <>
            <FormControl>
              <RadioGroup
                aria-labelledby="demo-controlled-radio-buttons-group"
                name="controlled-radio-buttons-group"
                value={selectedModel}
                onChange={handleModelChange}
              >
                {vehicleModels.map((model) => (
                  <FormControlLabel key={model} value={model} control={<Radio />} label={model} />
                ))}
              </RadioGroup>
            </FormControl>
          </>
        );
      case 4:
        return (
          <>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                startText="Start Date"
                endText="End Date"
                value={selectedDates}
                onChange={handleDateChange}
                renderInput={(startProps, endProps) => (
                  <>
                    <TextField {...startProps} variant="outlined" margin="normal" fullWidth />
                    <TextField {...endProps} variant="outlined" margin="normal" fullWidth />
                  </>
                )}
              />
            </LocalizationProvider>
          </>
        );
      default:
        return 'unknown step';
    }
  }

  const isStepOptional = (step) => {
    return step === 1 || step === 2 || step === 3;
  };

  const isStepSkipped = (step) => {
    return skippedSteps.includes(step);
  };

  const handleNext = () => {
    setActiveStep(activeStep + 1);
    setSkippedSteps(skippedSteps.filter((skipItem) => skipItem !== activeStep));
  };

  const handleBack = () => {
    setActiveStep(activeStep - 1);
  };

  const handleSkip = () => {
    if (!isStepSkipped(activeStep)) {
      setSkippedSteps([...skippedSteps, activeStep]);
    }
    setActiveStep(activeStep + 1);
  };

  return (
    <div>
      <Stepper alternativeLabel activeStep={activeStep}>
        {getSteps().map((step, index) => {
          const labelProps = {};
          const stepProps = {};
          if (isStepOptional(index)) {
            labelProps.optional = (
              <Typography variant="caption" align="center" style={{ display: 'block' }}>
                optional
              </Typography>
            );
          }
          if (isStepSkipped(index)) {
            stepProps.completed = false;
          }
          return (
            <Step {...stepProps} key={index}>
              <StepLabel {...labelProps}>{step}</StepLabel>
            </Step>
          );
        })}
      </Stepper>

      {activeStep === getSteps().length ? (
        <Typography variant="h3" align="center">
          Thank You
        </Typography>
      ) : (
        <>
          <form>{getStepContent(activeStep)}</form>
          <Button className={classes.button} disabled={activeStep === 0} onClick={handleBack}>
            Back
          </Button>
          {isStepOptional(activeStep) && (
            <Button
              className={classes.button}
              variant="contained"
              color="primary"
              onClick={handleSkip}
            >
              Skip
            </Button>
          )}
          <Button
            className={classes.button}
            variant="contained"
            color="primary"
            onClick={handleNext}
          >
            {activeStep === getSteps().length - 1 ? 'Finish' : 'Next'}
          </Button>
        </>
      )}
    </div>
  );
};

export default Firstpage;
